}

if (seen.courses.includes(courseId)) {
  return [];
}

seen.courses.push(courseId);

return [ { json: $json } ];

const items = $json.rss.channel.item;

return items.map(course => {
  const link = String(course.link || "");
  // strip origin
  const slug = link.replace(/^https?:\/\/www\.discudemy\.com\//, "").replace(/^\/+/, "");
  // last part after category
  const lastPart = slug.split("/").filter(Boolean).pop();
  const goLink = `https://www.discudemy.com/go/${lastPart}`;

  return {
    json: {
      ...course,
      goLink
    }
  };
});

const items = $items("Fetch Go Page");

return items.map((item, index) => {
  const html = item.json.data;

  const regex = /https:\/\/www\.udemy\.com\/course\/[^\s"'<>?]+[^"'<>]*/g;
  const match = html.match(regex);

  return {
    json: {
      index,
      udemyLink: match ? match[0] : null
    }
  };
});

// Extract Udemy image URL from Discudemy page HTML - PRESERVE UNDERSCORES
const items = $input.all();
const courseItems = $items('Merge Course + Udemy1');

return items.map((item, index) => {
  const courseData = courseItems[index].json;
  const html = item.json.data || item.json || '';

  let udemyImageUrl = null;

  // Method 1: Direct Udemy CDN patterns for 480x270 images - DON'T MODIFY THE URL
  const cdnPattern = /https:\/\/img-[a-z]\.udemycdn\.com\/course\/480x270\/[0-9]+_[a-zA-Z0-9_]+\.(jpg|jpeg|png|webp)/i;
  let match = html.match(cdnPattern);

  if (match) {
    udemyImageUrl = match[0];
  } else {
    // Method 2: Any Udemy CDN image - DON'T MODIFY THE URL
    const anyUdemyPattern = /https:\/\/img-[a-z]\.udemycdn\.com\/course\/[^\/]+\/[0-9]+_[a-zA-Z0-9_]+\.(jpg|jpeg|png|webp)/gi;
    const matches = html.match(anyUdemyPattern);

    if (matches && matches.length > 0) {
      // Get the first match - PRESERVE ORIGINAL URL WITH UNDERSCORES
      udemyImageUrl = matches[0];
    } else {
      // Method 3: Look in meta tags
      const metaPatterns = [
        /<meta\s+property="og:image"\s+content="([^"]+udemycdn[^"]+)"/i,
        /<meta\s+name="twitter:image"\s+content="([^"]+udemycdn[^"]+)"/i
      ];

      for (const pattern of metaPatterns) {
        match = html.match(pattern);
        if (match) {
          udemyImageUrl = match[1]; // PRESERVE ORIGINAL URL
          break;
        }
      }

      // Method 4: Look for any image in img tags
      if (!udemyImageUrl) {
        const imgPattern = /<img[^>]+src="([^"]+udemycdn[^"]+)"/gi;
        const imgMatches = [...html.matchAll(imgPattern)];
        if (imgMatches.length > 0) {
          udemyImageUrl = imgMatches[0][1]; // PRESERVE ORIGINAL URL
        }
      }
    }
  }

  // Clean URL - but preserve underscores
  if (udemyImageUrl) {
    udemyImageUrl = udemyImageUrl.split('?')[0];
  }

  console.log('Extracted image for course:', courseData.title, '- Image:', udemyImageUrl);

  return {
    json: {
      ...courseData,
      udemyImageUrl: udemyImageUrl,
      extractedUdemyImageUrl: udemyImageUrl,
      hasUdemyImage: !!udemyImageUrl
    }
  };
});

// Get all existing udemyLinks from the sheet
const existingRecords = $items("Read Existing Courses1");
const existingLinks = new Set(
  existingRecords.map(record => record.json.udemyLink).filter(link => link)
);

// Get incoming courses
const incomingCourses = $items("Filter Valid Courses1");

// Filter out courses that already exist in the sheet
const newCourses = incomingCourses.filter(course => {
  const udemyLink = course.json.udemyLink;
  return udemyLink && !existingLinks.has(udemyLink);
});

return newCourses;

// Process category check results with exact matching - ENSURE ALL COURSES CONTINUE
const items = $input.all();
const courseItems = $items('Filter New Courses Only1');

// Ensure we only process matching items
const minLength = Math.min(items.length, courseItems.length);

return items.slice(0, minLength).map((item, index) => {
  const courseData = courseItems[index].json;
  const response = item.json;

  let categoryId = null;

  // Safely extract category name - handle both string and object types
  let categoryName = '';
  if (typeof courseData.category === 'string') {
    categoryName = courseData.category;
  } else if (courseData.category && typeof courseData.category === 'object') {
    // If it's an object, try to get the text content
    categoryName = courseData.category._ || courseData.category['#text'] || courseData.category.text || '';
  }

  const searchCategory = categoryName.toLowerCase().trim();

  // Check if category already exists with exact name match
  if (Array.isArray(response) && response.length > 0) {
    // Find exact match (case insensitive)
    const exactMatch = response.find(cat => {
      const catName = (cat.name || '').toLowerCase().trim();
      return catName === searchCategory && searchCategory !== '';
    });
    categoryId = exactMatch ? exactMatch.id : null;
  } else if (response && response.id) {
    categoryId = response.id;
  }

  console.log('Category check for:', categoryName, '- Found ID:', categoryId, '- Exact match:', !!categoryId);

  return {
    json: {
      ...courseData,
      category: categoryName, // Ensure category is always a string
      existingCategoryId: categoryId,
      needsCategoryCreation: Boolean(!categoryId && searchCategory),
      ldCategoryId: categoryId || null,
      categoryName: searchCategory
    }
  };
});

// Extract new category ID and merge with original data - with error handling
const items = $input.all();
const courseItems = $items('Filter Needs Category Creation');

return items.map((item, index) => {
  const courseData = courseItems[index].json;
  const response = item.json;

  // If creation failed with "term exists" error, try to fetch the existing category
  if (response && response.code === 'term_exists') {
    console.log('Category already exists, using existing ID:', response.data.term_id);
    return {
      json: {
        ...courseData,
        ldCategoryId: response.data.term_id,
        categoryCreated: false, // wasn't created by us
        categoryAlreadyExists: true
      }
    };
  }

  if (response && response.id) {
    return {
      json: {
        ...courseData,
        ldCategoryId: response.id,
        categoryCreated: true
      }
    };
  }

  // If we get here, category creation failed for another reason
  console.log('Category creation failed:', response);
  return {
    json: {
      ...courseData,
      categoryCreated: false,
      creationError: response
    }
  };
});

// Extract LearnDash course link from response - Process ALL items
const allResponses = $input.all();
const allOriginalData = $items("Merge Category Data");
const results = [];

for (let i = 0; i < allResponses.length; i++) {
  const response = allResponses[i].json;
  const originalCourse = allOriginalData[i].json;

  if (response && response.id) {
    const learnDashLink = `https://courselamy.site/courses/${response.slug || response.id}/`;

    results.push({
      json: {
        ...originalCourse,
        learnDashLink: learnDashLink,
        learnDashId: response.id,
        learnDashSlug: response.slug
      }
    });
  } else {
    results.push({
      json: {
        ...originalCourse,
        learnDashLink: null,
        learnDashId: null
      }
    });
  }
}

return results;

