// Extract confidence level from AI response and filter
const items = $input.all();
const filtered = [];

for (const item of items) {
  const output = item.json.output || '';
  const symbol = item.json.symbol || 'UNKNOWN';

  // Try to extract confidence level from various formats
  let confidence = 0;

  // Match patterns like "Confidence: 85%" or "CONFIDENCE LEVEL: 85%"
  const confMatch = output.match(/confidence[:\s]+([0-9]+)%/i);
  if (confMatch) {
    confidence = parseInt(confMatch[1]);
  }

  // Extract trading signal
  let signal = 'HOLD';
  if (output.match(/TRADING SIGNAL[:\s]+BUY/i) || output.match(/Signal[:\s]+BUY/i)) {
    signal = 'BUY';
  } else if (output.match(/TRADING SIGNAL[:\s]+SELL/i) || output.match(/Signal[:\s]+SELL/i)) {
    signal = 'SELL';
  }

  // Filter: Only keep BUY/SELL signals with confidence >= 70%
  if ((signal === 'BUY' || signal === 'SELL') && confidence >= 70) {
    filtered.push({
      json: {
        symbol: symbol,
        signal: signal,
        confidence: confidence,
        analysis: output,
        timestamp: item.json.timestamp || new Date().toISOString(),
        alertMessage: `ðŸš¨ HIGH CONFIDENCE SIGNAL ðŸš¨\n\n` +
                     `Symbol: ${symbol}\n` +
                     `Signal: ${signal}\n` +
                     `Confidence: ${confidence}%\n\n` +
                     `${output.substring(0, 800)}${output.length > 800 ? '...' : ''}`
      }
    });
  }
}

return filtered;

