const CONFIG = {
  symbols: ['MERLUSDT', 'ZKJUSDT', 'BUSDT', 'KERNELUSDT', 'RESOLVUSDT', 'HYPERUSDT', 'MILKUSDT', 'OBOLUSDT', 'UNIUSDT', 'DEEPUSDT', 'ALPINEUSDT', 'WALUSDT', 'FORTHUSDT', 'SKATEUSDT', 'PROMPTUSDT', 'GUNUSDT', 'BABYUSDT', 'STOUSDT', 'FISUSDT', 'ASRUSDT', 'MLNUSDT', 'TANSSIUSDT', 'AIOTUSDT', 'SKYAIUSDT'],
  percentageThreshold: 5,
  candleCount: 10,
  retraceCount: 5,
  bollingerPeriod: 20,
  bollingerStdDev: 2,
  proximityThreshold: 0.02,
  interval: '15m'
};

return [
  {
    json: {
      config: CONFIG,
