    }
  }
];

const config = items[0].json.config;
const outputs = [];

config.symbols.forEach((symbol, index) => {
  outputs.push({
    json: {
      symbol: symbol,
      config: config,
      symbolIndex: index
    }
  });
});

return outputs;

const symbol = items[0].json.symbol;
const config = items[0].json.config;
const klines = items[0].json;

if (!Array.isArray(klines) || klines.length === 0) {
  return [{
    json: {
      symbol: symbol,
      error: 'No klines data available',
      timestamp: new Date().toISOString()
    }
  }];
}

const candles = klines.map(k => ({
  time: k[0],
  open: parseFloat(k[1]),
  high: parseFloat(k[2]),
  low: parseFloat(k[3]),
  close: parseFloat(k[4]),
  volume: parseFloat(k[5])
})).reverse();

function calculateBollingerBands(closes, period = 20, stdDev = 2) {
  if (closes.length < period) return null;

  const relevantCloses = closes.slice(-period);
  const mean = relevantCloses.reduce((sum, close) => sum + close, 0) / period;

  const variance = relevantCloses.reduce((sum, close) => sum + Math.pow(close - mean, 2), 0) / period;
  const standardDeviation = Math.sqrt(variance);

  return {
    upper: mean + (standardDeviation * stdDev),
    middle: mean,
    lower: mean - (standardDeviation * stdDev)
  };
}

const candlesWithBB = candles.map((candle, index) => {
  const closes = candles.slice(0, index + 1).map(c => c.close);
  const bb = calculateBollingerBands(closes, config.bollingerPeriod, config.bollingerStdDev);

  return {
    ...candle,
    bb: bb,
    isGreen: candle.close > candle.open,
    isRed: candle.close < candle.open,
    percentChange: ((candle.close - candle.open) / candle.open) * 100
  };
}).filter(c => c.bb !== null);

if (candlesWithBB.length < config.candleCount + config.retraceCount) {
  return [{
    json: {
      symbol: symbol,
      error: 'Insufficient data for analysis',
      timestamp: new Date().toISOString()
    }
  }];
}

const recentCandles = candlesWithBB.slice(0, config.candleCount + config.retraceCount);
const initialCandles = recentCandles.slice(config.retraceCount);
const retraceCandles = recentCandles.slice(0, config.retraceCount);
const currentCandle = candlesWithBB[0];

let strongUpMove = false;
let strongDownMove = false;
let initialDirection = null;

const initialStart = initialCandles[initialCandles.length - 1].close;
const initialEnd = initialCandles[0].close;
const totalMovement = ((initialEnd - initialStart) / initialStart) * 100;

if (Math.abs(totalMovement) >= config.percentageThreshold) {
  if (totalMovement > 0) {
    strongUpMove = true;
    initialDirection = 'up';
  } else {
    strongDownMove = true;
    initialDirection = 'down';
  }
}

let hasRetraced = false;
let retraceDirection = null;

if (strongUpMove || strongDownMove) {
  const retraceStart = retraceCandles[retraceCandles.length - 1].close;
  const retraceEnd = retraceCandles[0].close;
  const retraceMovement = ((retraceEnd - retraceStart) / retraceStart) * 100;

  if (strongUpMove && retraceMovement < -2) {
    hasRetraced = true;
    retraceDirection = 'down';
  } else if (strongDownMove && retraceMovement > 2) {
    hasRetraced = true;
    retraceDirection = 'up';
  }
}

const currentPrice = currentCandle.close;
const currentMiddleBB = currentCandle.bb.middle;
const distanceFromMiddleBB = Math.abs(currentPrice - currentMiddleBB) / currentMiddleBB;
const nearMiddleBB = distanceFromMiddleBB <= config.proximityThreshold;

let signal = null;
let signalStrength = 0;

if ((strongUpMove || strongDownMove) && hasRetraced && nearMiddleBB) {
  if (strongUpMove) {
    signal = 'BUY';
    signalStrength = Math.abs(totalMovement) * (1 - distanceFromMiddleBB);
  } else {
    signal = 'SELL';
    signalStrength = Math.abs(totalMovement) * (1 - distanceFromMiddleBB);
  }
}

return [{
  json: {
    symbol: symbol,
    signal: signal,
    signalStrength: signalStrength,
    analysis: {
      initialDirection: initialDirection,
      totalMovement: totalMovement.toFixed(2),
      hasRetraced: hasRetraced,
      retraceDirection: retraceDirection,
      nearMiddleBB: nearMiddleBB,
      distanceFromMiddleBB: (distanceFromMiddleBB * 100).toFixed(2),
      currentPrice: currentPrice,
      middleBB: currentMiddleBB.toFixed(4),
      upperBB: currentCandle.bb.upper.toFixed(4),
      lowerBB: currentCandle.bb.lower.toFixed(4)
    },
    candleData: {
      totalCandles: candlesWithBB.length,
      initialCandles: initialCandles.length,
      retraceCandles: retraceCandles.length
    },
    timestamp: new Date().toISOString(),
    config: config
  }
}];

