// Get first X courses (e.g., first 10 courses)
const items = $input.all();
const maxCourses = 10; // Change this number as needed

return items.slice(0, maxCourses).map(item => {
  const json = item.json;

  return {
    json: {
      ...json,
      goLink: json.goLink,
      courseId: json.guid || json.link || json.title,
      needsImageExtraction: true
    }
  };
});

// Extract Udemy image URL from Discudemy page HTML
const items = $input.all();

return items.map(item => {
  const courseData = item.json;
  const html = item.json.data || ''; // HTML from the HTTP request

  let udemyImageUrl = null;

  // Pattern 1: Look for Udemy CDN images directly
  const udemyCdnPatterns = [
    /https:\/\/img-c\.udemycdn\.com\/course\/[^"'\s>]+/gi,
    /https:\/\/img-b\.udemycdn\.com\/course\/[^"'\s>]+/gi,
    /https:\/\/udemy-images\.udemy-com\/course\/[^"'\s>]+/gi
  ];

  // Pattern 2: Look for og:image meta tag with Udemy image
  const metaPatterns = [
    /<meta[^>]*property="og:image"[^>]*content="([^"]*udemy[^"]+)"/gi,
    /<meta[^>]*name="twitter:image"[^>]*content="([^"]*udemy[^"]+)"/gi
  ];

  // Pattern 3: Look for data-src or src attributes with Udemy images
  const imgPatterns = [
    /<img[^>]*src="([^"]*udemy[^"]*\.(jpg|jpeg|png|webp))"[^>]*>/gi,
    /<img[^>]*data-src="([^"]*udemy[^"]*\.(jpg|jpeg|png|webp))"[^>]*>/gi,
    /<img[^>]*data-lazy="([^"]*udemy[^"]*\.(jpg|jpeg|png|webp))"[^>]*>/gi
  ];

  // Try Udemy CDN patterns first (most reliable)
  for (const pattern of udemyCdnPatterns) {
    const matches = [...html.matchAll(pattern)];
    if (matches.length > 0) {
      udemyImageUrl = matches[0][0];
      break;
    }
  }

  // Try meta tags
  if (!udemyImageUrl) {
    for (const pattern of metaPatterns) {
      const matches = [...html.matchAll(pattern)];
      if (matches.length > 0) {
        udemyImageUrl = matches[0][1];
        break;
      }
    }
  }

  // Try image tags
  if (!udemyImageUrl) {
    for (const pattern of imgPatterns) {
      const matches = [...html.matchAll(pattern)];
      if (matches.length > 0) {
        udemyImageUrl = matches[0][1];
        break;
      }
    }
  }

  // If we found a Udemy image, ensure it's the high-quality version
  if (udemyImageUrl) {
    // Replace low-quality versions with high-quality ones
    udemyImageUrl = udemyImageUrl
      .replace(/_[0-9]+x[0-9]+\./, '_480x270.') // Ensure 480x270 resolution
      .replace(/\/240x135\//, '/480x270/') // Replace low-res path
      .replace(/\/750x422\//, '/480x270/'); // Replace other resolutions
  }

  return {
    json: {
      ...courseData,
      extractedUdemyImageUrl: udemyImageUrl,
      hasUdemyImage: !!udemyImageUrl,
      imageSource: udemyImageUrl ? 'udemy_cdn' : 'not_found',
      discudemyPageHtml: html.substring(0, 500) + '...' // Store snippet for debugging
    }
  };
});

// Process downloaded Udemy image and prepare for storage
const items = $input.all();

return items.map(item => {
  const courseData = item.json;
  const imageData = item.binary;

  // Get the binary data key (it's dynamic)
  const binaryKey = Object.keys(imageData)[0];

  // Create a safe filename using course title
  const courseTitle = courseData.title ? 
    courseData.title.replace(/^\[100% OFF\]\s*/, '') // Remove [100% OFF] prefix
      .replace(/[^a-zA-Z0-9\s]/g, '') // Remove special characters
      .replace(/\s+/g, '_') // Replace spaces with underscores
      .substring(0, 40) : 
    'udemy_course';

  const timestamp = new Date().getTime();
  const filename = `${courseTitle}_${timestamp}.jpg`;

  return {
    json: {
      ...courseData,
      downloadedImage: true,
      localImagePath: `/course_images/${filename}`,
      imageFilename: filename,
      imageSize: imageData[binaryKey].data.length,
      imageType: imageData[binaryKey].mimeType
    },
    binary: {
      [binaryKey]: {
        ...imageData[binaryKey],
        fileName: filename
      }
    }
  };
});

